     7	contract VotingSystem is Ownable, ReentrancyGuard {
     8	    struct Candidate {
     9	        uint256 id;
    10	        string name;
    11	        string imageUrl;
    12	        uint256 voteCount;
    13	        bool exists;
    14	    }
