    16	    struct Voter {
    17	        string rwandanId;
    18	        string fullName;
    19	        string gender;
    20	        bool isRegistered;
    21	        bool hasVoted;
    22	        uint256 votedCandidateId;
    23	    }
    24	
    25	    mapping(string => Voter) public votersByID;
    26	    mapping(address => string) public addressToID;
    27	    mapping(uint256 => Candidate) public candidates;
    28	    string[] public registeredIDs;
    29	    
    30	    uint256 public candidatesCount;
    31	    uint256 public totalVotes;
    32	    bool public votingActive;
    33	
    34	    event VoterRegistered(string indexed rwandanId, string fullName);
    35	    event CandidateAdded(uint256 indexed candidateId, string name);
    36	    event VoteCast(string indexed rwandanId, uint256 indexed candidateId);
    37	    event VotingStarted();
    38	    event VotingEnded();
    39	
    40	    modifier onlyRegisteredVoter() {
    41	        string memory voterID = addressToID[msg.sender];
    42	        require(bytes(voterID).length > 0 && votersByID[voterID].isRegistered, "You are not registered to vote");
    43	        _;
    44	    }
    45	    
    46	    modifier onlyDuringVoting() {
    47	        require(votingActive, "Voting is not active");
    48	        _;
    49	    }
    50	    
    51	    modifier hasNotVoted() {
    52	        string memory voterID = addressToID[msg.sender];
    53	        require(!votersByID[voterID].hasVoted, "You have already voted");
    54	        _;
    55	    }
    56	
    57	    constructor() {
    58	        votingActive = false;
    59	        // Initialize the three candidates for Rwanda election
    60	        _addCandidate("KAGAME PAUL", "");
    61	        _addCandidate("ANDY ISHIMWE", "");
    62	        _addCandidate("ISHIMWE GHISLAIN", "");
    63	    }
    64	    
    65	    // Register a voter with Rwandan ID (only owner can register voters)
    66	    function registerVoter(
    67	        string memory _rwandanId,
    68	        string memory _fullName,
    69	        string memory _gender,
    70	        address _voterAddress
    71	    ) external onlyOwner {
    72	        require(bytes(_rwandanId).length == 16, "Rwandan ID must be 16 digits");
    73	        require(bytes(_fullName).length > 0, "Full name cannot be empty");
    74	        require(
    75	            keccak256(abi.encodePacked(_gender)) == keccak256(abi.encodePacked("Male")) ||
    76	            keccak256(abi.encodePacked(_gender)) == keccak256(abi.encodePacked("Female")),
    77	            "Gender must be Male or Female"
    78	        );
    79	        require(!votersByID[_rwandanId].isRegistered, "Voter with this ID is already registered");
    80	        require(bytes(addressToID[_voterAddress]).length == 0, "Address already linked to another ID");
    81	
    82	        votersByID[_rwandanId] = Voter({
    83	            rwandanId: _rwandanId,
    84	            fullName: _fullName,
    85	            gender: _gender,
    86	            isRegistered: true,
    87	            hasVoted: false,
    88	            votedCandidateId: 0
    89	        });
    90	
    91	        addressToID[_voterAddress] = _rwandanId;
    92	        registeredIDs.push(_rwandanId);
    93	
    94	        emit VoterRegistered(_rwandanId, _fullName);
    95	    }
